mapper.select:
SELECT UPRODUCT_NO, SUBJECT, NICKNAME, UPRODUCT_CD, PRICE, INFO, AREA, LOCATION, MAINPHOTO, PHOTO
FROM USED_PRODUCT
WHERE UPRODUCT_NO = #{uproduct_no}

SELECT MAINPHOTO
FROM USED_PRODUCT
WHERE UPRODUCT_NO = #{uproduct_no}

SELECT PHOTO
FROM USED_PRODUCT
WHERE UPRODUCT_NO = #{uproduct_no}

mapper.update:
UPDATE USED_PRODUCT
SET SUBJECT = #{subject}
   ,PRICE = #{price}
   ,INFO = #{info}
   ,MAINPHOTO = #{mainphoto}
   ,PHOTO = #{photo}
WHERE UPRODUCT_NO= #{uproduct_no}
