mapper.select:
SELECT UPRODUCT_NO, SUBJECT, NICKNAME, UPRODUCT_CD, PRICE, INFO, AREA, LOCATION, MAINPHOTO, PHOTO
FROM USED_PRODUCT
WHERE UPRODUCT_NO = #{UPRODUCT_NO}

SELECT MAINPHOTO
FROM USED_PRODUCT
WHERE UPRODUCT_NO = #{UPRODUCT_NO}

SELECT PHOTO
FROM USED_PRODUCT
WHERE UPRODUCT_NO = #{UPRODUCT_NO}

mapper.update:
UPDATE USED_PRODUCT
SET SUBJECT = #{SUBJECT}
   ,PRICE = #{PRICE}
   ,INFO = #{INFO}
   ,MAINPHOTO = #{MAINPHOTO}
   ,PHOTO = #{PHOTO}
WHERE UPRODUCT_NO= #{UPRODUCT_NO}
