mapper.select:
SELECT MAINPHOTO
FROM USED_PRODUCT
WHERE UPRODUCT_NO = #{uproduct_no}

SELECT PHOTO
FROM USED_PRODUCT
WHERE UPRODUCT_NO = #{uproduct_no}

mapper.update:
UPDATE USED_PRODUCT
SET SUBJECT = #{subject}
   ,PRICE = #{price}
   ,INFO = #{info}
   ,STATUS = #{status}
   ,MAINPHOTO = #{mainphoto}
   ,PHOTO = #{photo}
WHERE UPRODUCT_NO= #{uproduct_no}
